import React, { useState } from "react";
import "./Switcher.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMoon, faSun } from "@fortawesome/free-solid-svg-icons";
import Language from "../Language/Language";
function Switcher(props) {
  // Open Button Setting
  const [classBtnSetting, setSetting] = useState(false);
  function setStateBtnSetting() {
    setSetting(!classBtnSetting);
  }
  function Open_CloseSetting(value) {
    if (value === false) return "style-switcher closeSwitcherSetting";
    else return "style-switcher openSwitcherSetting";
  }
  // Button Change Color Button
  const [btnColor, setBtnColor] = useState("pink");
  setBtnColor(value)
  {
    setBtnColor(value);
  }
  console.log(btnColor);
  return (
    <div className={Open_CloseSetting(classBtnSetting)}>
      <div className="toggle-style-switcher" onClick={setStateBtnSetting}>
        <i className="fa fa-cog fa-spin"></i>
      </div>
      <h5>Style Switcher</h5>
      <ul>
        <li>
          <p style={{ backgroundColor: "#ec1839" }} onClick={()=>{setBtnColor("pink")}}>
            {""}
          </p>
        </li>
        <li>
          <p style={{ backgroundColor: "#2196f3" }} onClick={()=>{setBtnColor("blue")}}>
            {""}
          </p>
        </li>
        <li>
          <p style={{ backgroundColor: "#fa5b0f" }} onClick={()=>{setBtnColor("orange")}}>
            {""}
          </p>
        </li>
        <li>
          <p style={{ backgroundColor: "#ffb400" }} onClick={()=>{setBtnColor("yellow")}}>
            {""}
          </p>
        </li>
        <li>
          <p style={{ backgroundColor: "#72b626" }} onClick={()=>{setBtnColor("green")}}>
            {""}
          </p>
        </li>
      </ul>
      <h5>Theme</h5>
      <div className="ThemeSwitch">
        <input type="checkbox" className="checkboxTheme" id="chk" />
        <label className="labelTheme" htmlFor="chk">
          <FontAwesomeIcon icon={faMoon} />
          <FontAwesomeIcon icon={faSun} />
          <div className="ball"></div>
        </label>
      </div>
      <h5>Language</h5>
      <Language />
    </div>
  );
}
export default Switcher;
