import React, { useState, useEffect } from "react";
import ContactInfo from "./ContactInfo";
import Rocket from "../../image/rocket.png";

import "./Contact.css";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import AccountCircle from "@material-ui/icons/AccountCircle";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import FormControl from "@material-ui/core/FormControl";
import HttpsIcon from "@material-ui/icons/Https";
import SubjectIcon from "@material-ui/icons/Subject";
import TitleIcon from "@material-ui/icons/Title";
import Button from "@material-ui/core/Button";
import SendIcon from "@material-ui/icons/Send";
// NodeMailer
const nodemailer = require("nodemailer");
// import clsx from 'clsx';
// Style TextField
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
    margin: "normal",
  },
  margin: {
    margin: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
}));
function Contact() {
  // Translation
  // const { t } = useTranslation();
  // Pre loading Component
  const [isLoading, setStateLoading] = useState(false);
  useEffect(() => {
    const timer = setTimeout(() => {
      setStateLoading(false); // Set lai isLoading sau 2s
    }, 1500);
    return () => clearTimeout(timer);
  }, [isLoading]);
  function changePreLoader(value) {
    if (value === false) {
      return "preloader opacity-0";
    } else return "preloader";
  }
  //   Scroll Page

  var [valueScroll, setScrollValue] = useState(0);
  window.onscroll = function () {
    ScrollIndicator();
  };
  function ScrollIndicator() {
    var winScroll =
      document.body.scrollTop || document.documentElement.scrollTop;
    var height =
      document.documentElement.scrollHeight -
      document.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    setScrollValue(scrolled);
    // document.getElementById("myBar").style.width = scrolled + "%";
  }

  //   Style TextField
  const [values, setValues] = useState({
    showPassword: false,
  });
  const handleChangePassWord = (prop) => (event) => {
    setPassWord(event.target.value);
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };
  const classes = useStyles();

  // Xử lý handleChange Form
  const [email, setEmail] = useState("");
  const [password, setPassWord] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  function onHandleChangeInputEmail(event) {
    console.log(event.target.value);
    setEmail(event.target.value);
  }
  function onHandleChangeInputMessage(event) {
    console.log(event.target.value);
    setMessage(event.target.value);
  }
  function onHandleChangeInputSubject(event) {
    console.log(event.target.value);
    setSubject(event.target.value);
  }
  var mailOptions = {
    from: "letanco1111@gmail.com",
    to: "letangco5@gmail.com",
    subject: "Test",
    text: "Hello World!",
  };
  var transporter = nodemailer.createTransport({
    host: "smtp.gmail.com",
    port: 587,
    secure: false,
    requireTLS: true,
    auth: {
      user: "letanco1111@gmail.com",
      pass: "Lyson002",
    },
  });
  function onSubmitContact() {
    console.log(email, password, subject, message);
    console.log(transporter)
    transporter.sendMail(mailOptions, (error, info) => {
      if (error) {
        return console.log(error.message);
      }
      console.log("success");
    });
  }
  return (
    <>
      <div className={changePreLoader(isLoading)}>
        <div className="loader">
          <img src={Rocket} alt="RocketLoading" width="70px" />
        </div>
      </div>
      <div
        className="header-scroll"
        style={{ marginLeft: "0px", padding: "0px" }}
      >
        <div className="progress-container">
          <div
            className="progress-bar-scroll"
            id="myBar"
            style={{ width: valueScroll.toString() + "%" }}
          ></div>
        </div>
      </div>
      {/* Content Contact */}
      <section className="contact section-contact">
        <div className="row-about">
          <div className="section-title padd-15">
            <h2>Contact</h2>
          </div>
        </div>
        <section className="row-about content-contact">
          <ContactInfo />
          <div className="contact-filter padd-15">
            {/* Form */}
            <form className={classes.root} noValidate autoComplete="on">
              <div className="textField-Email col-xs-12 col-sm-6 col-md-6 col-lg-6">
                <Grid container spacing={1} alignItems="flex-end">
                  <Grid item>
                    <AccountCircle htmlFor="input-with-icon-grid" />
                  </Grid>
                  <Grid item>
                    <TextField
                      id="input-with-icon-grid"
                      label="Email"
                      onChange={onHandleChangeInputEmail}
                    />
                  </Grid>
                </Grid>
              </div>
              <div className="textField-Password col-xs-12 col-sm-6 col-md-6 col-lg-6">
                <Grid container spacing={1} alignItems="flex-end">
                  <Grid item>
                    <HttpsIcon />
                  </Grid>
                  <Grid item>
                    <FormControl
                    // className={clsx(classes.margin, classes.textField)}
                    >
                      <InputLabel htmlFor="standard-adornment-password">
                        Password
                      </InputLabel>
                      <Input
                        id="standard-adornment-password"
                        type={values.showPassword ? "text" : "password"}
                        value={values.password}
                        onChange={handleChangePassWord("password")}
                        endAdornment={
                          <InputAdornment position="end">
                            <IconButton
                              aria-label="toggle password visibility"
                              onClick={handleClickShowPassword}
                              onMouseDown={handleMouseDownPassword}
                            >
                              {values.showPassword ? (
                                <Visibility />
                              ) : (
                                <VisibilityOff />
                              )}
                            </IconButton>
                          </InputAdornment>
                        }
                      />
                    </FormControl>
                  </Grid>
                </Grid>
              </div>

              <div className="textField-Subject col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <Grid container spacing={1} alignItems="flex-end">
                  <Grid item>
                    <TitleIcon />
                  </Grid>
                  <Grid item>
                    <TextField
                      id="input-with-icon-grid"
                      label="Subject"
                      onChange={onHandleChangeInputSubject}
                    />
                  </Grid>
                </Grid>
              </div>
              <div className="textField-Message col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <Grid container spacing={1} alignItems="flex-start">
                  <Grid item>
                    <SubjectIcon />
                  </Grid>
                  <Grid item>
                    <TextField
                      id="outlined-multiline-static"
                      label="Your Message..."
                      multiline
                      rows={4}
                      variant="outlined"
                      onChange={onHandleChangeInputMessage}
                      // className={classes.root}
                    />
                  </Grid>
                </Grid>
              </div>

              <div className="textField-Message col-xs-12 col-sm-12 col-md-12 col-lg-12">
                <Button
                  variant="contained"
                  color="secondary"
                  className={classes.button}
                  startIcon={<SendIcon />}
                  onClick={onSubmitContact}
                >
                  Send Message
                </Button>
              </div>
            </form>
          </div>
        </section>
      </section>
    </>
  );
}
export default Contact;
