import React, { useState, useEffect } from "react";
import Rocket from "../../image/rocket.png";
// import { useTranslation } from "react-i18next";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import "./Portfolio.css";
import All from "./All";
import DevDesign from "./DevDesign";
import Portfolio1 from '../../image/PortFolio_Covi.PNG';
import ModelPortfolio from './Modal/ModelPortfolio';
function Portfolio() {
  // Translation
  // const { t } = useTranslation();
  // Pre loading Component
  const [isLoading, setStateLoading] = useState(false);
  useEffect(() => {
    const timer = setTimeout(() => {
      setStateLoading(false); // Set lai isLoading sau 2s
    }, 1500);
    return () => clearTimeout(timer);
  }, [isLoading]);
  function changePreLoader(value) {
    if (value === false) {
      return "preloader opacity-0";
    } else return "preloader";
  }
  //   Scroll Page

  var [valueScroll, setScrollValue] = useState(0);
  window.onscroll = function () {
    ScrollIndicator();
  };
  function ScrollIndicator() {
    var winScroll =
      document.body.scrollTop || document.documentElement.scrollTop;
    var height =
      document.documentElement.scrollHeight -
      document.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    setScrollValue(scrolled);
    // document.getElementById("myBar").style.width = scrolled + "%";
  }
  // Active li
  var [activeSelectPorfolio, setSelectPortfolio] = useState("all");
  function renderSeclect(value) {
    switch (value) {
      case "all":
        return <All />;
      case "web":
        return <DevDesign />;
      default:
        break;
    }
  }
  return (
    <>
      <div className={changePreLoader(isLoading)}>
        <div className="loader">
          <img src={Rocket} alt="RocketLoading" width="70px" />
        </div>
      </div>
      <div
        className="header-scroll"
        style={{ marginLeft: "0px", padding: "0px" }}
      >
        <div className="progress-container">
          <div
            className="progress-bar-scroll"
            id="myBar"
            style={{ width: valueScroll.toString() + "%" }}
          ></div>
        </div>
      </div>
      {/* <ModelPortfolio /> */}
      <section className="portfolio section-portfolio">
          <div className="row-about">
            <div className="section-title padd-15">
              <h2>Portfolio</h2>
            </div>
        </div>
        <div className="row-about">
          <div className="portfolio-filter padding-15">
            <button type="button">All</button>
            <button type="button">Web Design</button>
            <button type="button">Photography</button>
            <button type="button">Video Editing</button>
            <button type="button">Logo Design</button>
          </div>

          <div className="row list-item-portfolio padd-15">
            <div className="portfolio-item padd-15 col-xs-12 col-sm-6 col-md-6 col-lg-4">
              <div className="portfolio-item-inner shadow-dark">
                <div className="portfolio-img">
                  <img src={Portfolio1} alt="A"/>
                </div>
                <div className="portfolio-info">
                  <h4>Web Design</h4>
                  <div className="iconPortfolio">
                    <FontAwesomeIcon  className="icon" icon={faSearch} size="2x"/>
                  </div>
                </div>
              </div>
            </div>
            <div className="portfolio-item padd-15 col-xs-12 col-sm-6 col-md-6 col-lg-4">
              <div className="portfolio-item-inner shadow-dark">
                <div className="portfolio-img">
                  <img src={Portfolio1} alt="A"/>
                </div>
                <div className="portfolio-info">
                  <h4>Web Design</h4>
                  <div className="iconPortfolio">
                    <FontAwesomeIcon  className="icon" icon={faSearch} size="2x"/>
                  </div>
                </div>
              </div>
            </div>
            <div className="portfolio-item padd-15 col-xs-12 col-sm-6 col-md-6 col-lg-4">
              <div className="portfolio-item-inner shadow-dark">
                <div className="portfolio-img">
                  <img src={Portfolio1} alt="A"/>
                </div>
                <div className="portfolio-info">
                  <h4>Web Design</h4>
                  <div className="iconPortfolio">
                    <FontAwesomeIcon  className="icon" icon={faSearch} size="2x"/>
                  </div>
                </div>
              </div>
            </div>
            <div className="portfolio-item padd-15 col-xs-12 col-sm-6 col-md-6 col-lg-4">
              <div className="portfolio-item-inner shadow-dark">
                <div className="portfolio-img">
                  <img src={Portfolio1} alt="A"/>
                </div>
                <div className="portfolio-info">
                  <h4>Web Design</h4>
                  <div className="iconPortfolio">
                    <FontAwesomeIcon  className="icon" icon={faSearch} size="2x"/>
                  </div>
                </div>
              </div>
            </div>
            <div className="portfolio-item padd-15 col-xs-12 col-sm-6 col-md-6 col-lg-4">
              <div className="portfolio-item-inner shadow-dark">
                <div className="portfolio-img">
                  <img src={Portfolio1} alt="A"/>
                </div>
                <div className="portfolio-info">
                  <h4>Web Design</h4>
                  <div className="iconPortfolio">
                    <FontAwesomeIcon className="icon" icon={faSearch} size="2x"/>
                  </div>
                </div>
              </div>
            </div>
            <div className="portfolio-item padd-15 col-xs-12 col-sm-6 col-md-6 col-lg-4">
              <div className="portfolio-item-inner shadow-dark">
                <div className="portfolio-img">
                  <img src={Portfolio1} alt="A"/>
                </div>
                <div className="portfolio-info">
                  <h4>Web Design</h4>
                  <div className="iconPortfolio">
                    <FontAwesomeIcon className="icon" icon={faSearch} size="2x"/>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

    </>
  );
}

export default Portfolio;
