import React, { useState, useEffect } from "react";
import Rocket from "../../image/rocket.png";
import axios from 'axios';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPhone,
  faMapMarkerAlt,
  faEnvelope,
  faBlog,
} from "@fortawesome/free-solid-svg-icons";
import "./Contact.css";
import { reset } from "nodemon";

function Contact() {
  // Translation
  // const { t } = useTranslation();
  // Pre loading Component
  const [isLoading, setStateLoading] = useState(true);
  useEffect(() => {
    const timer = setTimeout(() => {
      setStateLoading(false); // Set lai isLoading sau 2s
    }, 1500);
    return () => clearTimeout(timer);
  }, [isLoading]);
  function changePreLoader(value) {
    if (value === false) {
      return "preloader opacity-0";
    } else return "preloader";
  }
  //   Scroll Page

  var [valueScroll, setScrollValue] = useState(0);
  window.onscroll = function () {
    ScrollIndicator();
  };
  function ScrollIndicator() {
    var winScroll =
      document.body.scrollTop || document.documentElement.scrollTop;
    var height =
      document.documentElement.scrollHeight -
      document.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    setScrollValue(scrolled);
    // document.getElementById("myBar").style.width = scrolled + "%";
  }

  //   Style TextField

  // Xử lý handleChange Form
  const [name, setName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [sent,setSent]=useState(false);
  function onHandleChangeInputName(event) {
    console.log(event.target.value);
    setName(event.target.value);
  }
  function onHandleChangeInputLastName(event) {
    console.log(event.target.value);
    setLastName(event.target.value);
  }
  function onHandleChangeInputEmail(event) {
    console.log(event.target.value);
    setEmail(event.target.value);
  }
  function onHandleChangeInputMessage(event) {
    console.log(event.target.value);
    setMessage(event.target.value);
  }
  function onHandleChangeInputSubject(event) {
    console.log(event.target.value);
    setSubject(event.target.value);
  }

  function formSubmit() {
    let data = {
      name: this.state.name,
      lastname: this.state.lastname,
      email: this.state.email,
      subject: this.state.subject,
      message: this.state.message,
    };

    axios
      .post("/api/forma", data)
      .then((res) => {
        // this.setState(
        //   {
        //     sent: true,
        //   },
        //   this.resetForm()
        // );
        setSent(true);
        resetForm();
      })
      .catch(() => {
        console.log("message not send");
      });

    function resetForm() {
      // this.setState({
      //   name: "",
      //   lastname: "",
      //   message: "",
      //   email: "",
      // });
      setName("");
      setLastName("");
      setMessage("");
      setEmail("");
      setSubject("");

      setTimeout(() => {
        setSent(false)
      }, 3000);
    };

  }
  return (
    <>
      <div className={changePreLoader(isLoading)}>
        <div className="loader">
          <img src={Rocket} alt="RocketLoading" width="70px" />
        </div>
      </div>
      <div
        className="header-scroll"
        style={{ marginLeft: "0px", padding: "0px" }}
      >
        <div className="progress-container">
          <div
            className="progress-bar-scroll"
            id="myBar"
            style={{ width: valueScroll.toString() + "%" }}
          ></div>
        </div>
      </div>
      {/* Content Contact */}
      <section className="portfolio section-portfolio">
        <div className="row-about">
          <div className="section-title padd-15">
            <h2>Contact Me</h2>
          </div>
        </div>
        <div className="row-about content-contact">
          <div className="containerContact">
            <div className="contactInfo">
              <div>
                <h2>Contact Info</h2>
                <ul className="info">
                  <li>
                    <span><FontAwesomeIcon className="icon-info-contact" icon={faPhone} /></span>
                    <span>(+84) 332.858.127</span>
                  </li>
                  <li>
                    <span><FontAwesomeIcon className="icon-info-contact" icon={faMapMarkerAlt} /></span>
                    <span>District 9, Ho Chi Minh City</span>
                  </li>
                  <li>
                    <span><FontAwesomeIcon className="icon-info-contact" icon={faEnvelope} /></span>
                    <span>letangco@gmail.com</span>
                  </li>
                </ul>
                <ul className="sci">
                  <li>
                    <a href="facebook">
                      <i className="fa fa-facebook" aria-hidden="true"></i>
                    </a>
                  </li>
                  <li>
                    <a href="facebook">
                      <i className="fa fa-instagram" aria-hidden="true"></i>
                    </a>
                  </li>
                  <li>
                    <a href="facebook">
                      <i className="fa fa-envelope" aria-hidden="true"></i>
                    </a>
                  </li>
                  <li>
                    <a href="facebook">
                      <FontAwesomeIcon icon={faBlog} />
                    </a>
                  </li>
                </ul>
              </div>
            </div>

            <div className="contactForm">
              <h2>Send a Message</h2>
              <div className="formBox">
                <div className="inputBox w50">
                  <input type="text" name="" required onChange={onHandleChangeInputName} />
                  <span>First Name</span>
                </div>
                <div className="inputBox w50">
                  <input type="text" name="" required onChange={onHandleChangeInputLastName} />
                  <span>Last Name</span>
                </div>
                <div className="inputBox w50">
                  <input type="text" name="" required onChange={onHandleChangeInputEmail} />
                  <span>Email Address</span>
                </div>
                <div className="inputBox w50">
                  <input type="text" name="" required onChange={onHandleChangeInputSubject} />
                  <span>Subject</span>
                </div>
                <div className="inputBox w100">
                  <textarea name="" required onChange={onHandleChangeInputMessage}></textarea>
                  <span>Write your message here...</span>
                </div>
                <div className="inputBox w100">
                  <input type="submit" value="Send" onClick={formSubmit} />
                </div>
              </div>
            </div>
          </div>

        </div>
      </section>


    </>
  );
}
export default Contact;
