import React,{useState} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUserTie, faBriefcase, faBlog, faComments } from '@fortawesome/free-solid-svg-icons';
import '../../style/Menu.css';
import '../../style/Skin/pink.css';
import About from '../About/About.js';
import Home from '../Home/Home.js';
import Switcher from '../SwitcherSetting/Switcher';
import {
    BrowserRouter as Router,
    Route,
    NavLink,
    Switch
  } from "react-router-dom";
function Menu(){
    const [onToggle, setStateToggle] = useState(false);
    function asideSectionToggleBtn(){
        console.log(1);
        
    }
    return (
        <Router>
        <div>
            {/* Main component */}
            <div className="main-container">
                {/* Aside */}
                <div className="aside open-toggle">
                    {/* Logo */}
                    <div className="logo">
                        <a href="a">
                            LE TANG CO
                        </a>
                    </div>
                    {/* Nav Toggle btn */}
                    <div className="nav-toggle" onClick={asideSectionToggleBtn}>
                        <span>

                        </span>
                    </div>
                    {/* Nav */}
                    <ul className="nav">
                        <li>
                            <NavLink to="/">
                            <FontAwesomeIcon icon={faHome} />  &nbsp;HOME
                            </NavLink>
                        </li>
                        <li>
                            <NavLink exact to="/about">
                            <FontAwesomeIcon icon={faUserTie} /> &nbsp; ABOUT
                            </NavLink>
                        </li>
                        <li>
                            <a href="home">
                                <FontAwesomeIcon icon={faBriefcase} />  &nbsp;PORTFOLIO
                            </a>
                        </li>
                        <li>
                            <a href="home">
                            <FontAwesomeIcon icon={faComments} /> &nbsp;CONTACT
                            </a>
                        </li>
                        <li>
                            <a href="home">
                                <FontAwesomeIcon icon={faBlog} /> &nbsp; MY BLOG
                            </a>
                        </li>
                    </ul>
                    {/* CopyRight */}
                    <div className="copyright-text">
                        &copy; 2020 Portfolio Le Tang Co
                    </div>
                </div>
                {/* Aside */}
                {/* Main content */}
                <div className="main-content">
                    <Switch>
                        <Route path="/" exact component={Home} />
                        <Route path="/about" exact component={About} />
                        
                    </Switch>
                </div>
                {/* Main content */}
            </div>
            {/* Switcher Setting */}
            <Switcher />
            {/* Main component */}
        </div>
        </Router>
    );
}
export default Menu;
