import React, { useState, useEffect } from "react";
import Rocket from "../../image/rocket.png";
import { useTranslation } from "react-i18next";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faDownload, faIdCardAlt } from "@fortawesome/free-solid-svg-icons";
import "./Portfolio.css";
function Portfolio() {
  // Translation
  const { t } = useTranslation();
  // Pre loading Component
  const [isLoading, setStateLoading] = useState(false);
  //   var isSetIsLoading = () => {
  //     setStateLoading(false);
  //   };
  //   setTimeout(isSetIsLoading, 1500);

  useEffect(() => {
    const timer = setTimeout(() => {
      setStateLoading(false); // Set lai isLoading sau 2s
    }, 1500);
    return () => clearTimeout(timer);
  }, [isLoading]);
  function changePreLoader(value) {
    if (value === false) {
      return "preloader opacity-0";
    } else return "preloader";
  }

  //   Scroll Page

  var [valueScroll, setScrollValue] = useState(0);
  window.onscroll = function () {
    ScrollIndicator();
  };
  function ScrollIndicator() {
    var winScroll =
      document.body.scrollTop || document.documentElement.scrollTop;
    var height =
      document.documentElement.scrollHeight -
      document.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    setScrollValue(scrolled);
    // document.getElementById("myBar").style.width = scrolled + "%";
  }
  return (
    <>
      <div className={changePreLoader(isLoading)}>
        <div className="loader">
          <img src={Rocket} alt="RocketLoading" width="70px" />
        </div>
      </div>
      <div
        className="header-scroll"
        style={{ marginLeft: "0px", padding: "0px" }}
      >
        <div className="progress-container">
          <div
            className="progress-bar"
            id="myBar"
            style={{ width: valueScroll.toString() + "%" }}
          ></div>
        </div>
      </div>

      <section className="about-section">
        <div className="container">
          <div className="row row-about">
            <div className="section-title">
              <h2>{t("menu.portfolio")}</h2>
            </div>
          </div>
          <div className="row row-about">
            <div className="about-content">
              <div className="row-about">
                <div className="about-text padd-15 option-portfolio">
                  <div>
                    <ul>
                      <li className="activePorfolio">All</li>
                      <li className="activePorfolio">Web Design</li>
                      <li className="activePorfolio">Photography</li>
                      <li>Video Editing</li>
                      <li>Logo Design</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Personal Info */}
          <div className="row-about">
            <div className="row personal-info col-xs-12 col-sm-12 col-md-12 col-lg-12">
              <div className="row-about">
                <div className="col-xs-12 col-sm-6 col-md-6 col-lg-4 other-item">
                  <div>
                    <p className="icon-other-item">
                      {/* <FontAwesomeIcon icon={faCameraRetro} size="2x" /> */}
                    </p>
                    <p>
                      Photography can only reproduce reality. But as soon as it
                      is captured, that reality becomes part of the past
                    </p>
                  </div>
                </div>
                <div className="col-xs-12 col-sm-6 col-md-6 col-lg-4 other-item">
                  <div>
                    <p className="icon-other-item">
                      {/* <FontAwesomeIcon icon={faCameraRetro} size="2x" /> */}
                    </p>
                    <h3>Photography</h3>
                    <p>
                      Photography can only reproduce reality. But as soon as it
                      is captured, that reality becomes part of the past
                    </p>
                  </div>
                </div>
                <div className="col-xs-12 col-sm-6 col-md-6 col-lg-4 other-item">
                  <div>
                    <p className="icon-other-item">
                      {/* <FontAwesomeIcon icon={faCameraRetro} size="2x" /> */}
                    </p>
                    <h3>Photography</h3>
                    <p>
                      Photography can only reproduce reality. But as soon as it
                      is captured, that reality becomes part of the past
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          </div>
      </section>
    </>
  );
}

export default Portfolio;
