import React, { useState, useEffect } from "react";
import Rocket from "../../image/rocket.png";
import AvatarContact from "../../image/Circle_LTC_AVT.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPhone,
  faMapMarkerAlt,
  faEnvelope,
} from "@fortawesome/free-solid-svg-icons";
import "./Contact.css";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
// Style TextField
const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
}));
function Contact() {
  // Translation
  // const { t } = useTranslation();
  // Pre loading Component
  const [isLoading, setStateLoading] = useState(true);
  useEffect(() => {
    const timer = setTimeout(() => {
      setStateLoading(false); // Set lai isLoading sau 2s
    }, 1500);
    return () => clearTimeout(timer);
  }, [isLoading]);
  function changePreLoader(value) {
    if (value === false) {
      return "preloader opacity-0";
    } else return "preloader";
  }
  //   Scroll Page

  var [valueScroll, setScrollValue] = useState(0);
  window.onscroll = function () {
    ScrollIndicator();
  };
  function ScrollIndicator() {
    var winScroll =
      document.body.scrollTop || document.documentElement.scrollTop;
    var height =
      document.documentElement.scrollHeight -
      document.documentElement.clientHeight;
    var scrolled = (winScroll / height) * 100;
    setScrollValue(scrolled);
    // document.getElementById("myBar").style.width = scrolled + "%";
  }

  //   Style TextField
  const classes = useStyles();
  return (
    <>
      <div className={changePreLoader(isLoading)}>
        <div className="loader">
          <img src={Rocket} alt="RocketLoading" width="70px" />
        </div>
      </div>
      <div
        className="header-scroll"
        style={{ marginLeft: "0px", padding: "0px" }}
      >
        <div className="progress-container">
          <div
            className="progress-bar-scroll"
            id="myBar"
            style={{ width: valueScroll.toString() + "%" }}
          ></div>
        </div>
      </div>
      {/* Content Contact */}
      <section className="contact section-contact">
        <div className="row-about">
          <div className="section-title padd-15">
            <h2>Contact</h2>
          </div>
        </div>
        <section className="row-about content-contact">
          <div className="img-avt-contact">
            <img src={AvatarContact} alt="AvatarContact" />
          </div>
          <div className="info-contact">
            <div className="phone-info col-xs-12 col-sm-12 col-md-4 col-lg-4">
              <FontAwesomeIcon className="icon-info-contact" icon={faPhone} />
              <h4>Call Me On</h4>
              <p>(+84) 332.858.127</p>
            </div>
            <div className="location-info col-xs-12 col-sm-12 col-md-4 col-lg-4">
              <FontAwesomeIcon
                className="icon-info-contact"
                icon={faMapMarkerAlt}
              />
              <h4>Address</h4>
              <p>District 9, Ho Chi Minh City</p>
            </div>
            <div className="email-info col-xs-12 col-sm-12 col-md-4 col-lg-4">
              <FontAwesomeIcon
                className="icon-info-contact"
                icon={faEnvelope}
              />
              <h4>Email</h4>
              <p>letangco@gmail.com</p>
            </div>
          </div>
          <div className="contact-filter padd-15">
            <form className={classes.margin} noValidate autoComplete="on">
              <Grid container spacing={1} alignItems="flex-end">
                <Grid item>
                  <AccountCircle />
                </Grid>
                <Grid item>
                  <TextField id="input-with-icon-grid" label="Email" />
                </Grid>
              </Grid>
              <Grid container spacing={1} alignItems="flex-end">
                <Grid item>
                  <AccountCircle />
                </Grid>
                <Grid item>
                  <TextField id="input-with-icon-grid" label="Email" />
                </Grid>
              </Grid>
              <InputLabel htmlFor="standard-adornment-password">Password</InputLabel>
          <Input
            id="standard-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
          />
            </form>
          </div>
        </section>
      </section>
    </>
  );
}
export default Contact;
